def say_hello(word):
    letters_count = {}

    for letter in word:
        if letter not in letters_count:
            letters_count[letter] = 0
        letters_count[letter] += 1
    remain_letters = dict(letters_count)

    for i in letters_count.keys():
        letters_count[i] //= 2

    def enough_remaining_letters(letter):
        return remain_letters[letter] - 1 >= letters_count[letter]

    result = []
    for letter in word[::-1]:
        if letters_count[letter] > 0:
            while result and result[-1] > letter and enough_remaining_letters(result[-1]):
                letters_count[result.pop()] += 1

            letters_count[letter] -= 1
            result.append(letter)

        remain_letters[letter] -= 1

    return "".join(result)


print(say_hello('abcdefgabcdefg'))  # agfedcb
print(say_hello('aeiouuoiea'))  # aeiou
print(say_hello('eggegg'))  # egg

print(say_hello(''))

# 

print(say_hello(''))

# 
